function doSomething () {
    projectile.setFlag(SpriteFlag.AutoDestroy, false)
    projectile.setFlag(SpriteFlag.DestroyOnWall, false)
    projectile.setFlag(SpriteFlag.BounceOnWall, true)
    projectile.lifespan = 5000
}
sprites.onOverlap(SpriteKind.ddd, SpriteKind.Player, function (sprite, otherSprite) {
    info.player1.changeLifeBy(-1)
    sprite.destroy()
})
info.player1.onLifeZero(function () {
    game.over(false, effects.melt)
})
info.player2.onLifeZero(function () {
    game.over(true, effects.confetti)
})
sprites.onOverlap(SpriteKind.Projectile, SpriteKind.Enemy, function (sprite, otherSprite) {
    tiles.placeOnRandomTile(otherSprite, sprites.dungeon.collectibleInsignia)
    info.player2.changeLifeBy(-1)
    sprite.destroy()
})
